<?phpglobal $wpdb;session_start();$frontimage	= get_option('girl_frontimage')?get_option('girl_frontimage'):"";$backimage	= get_option('girl_backimage')?get_option('girl_backimage'):"";if(isset($_POST['submit_coverform'])){	if(get_option('girl_frontimage', 'default') == 'default'){		add_option('girl_frontimage');	}	if(get_option('girl_backimage', 'default') == 'default'){		add_option('girl_backimage');	}		$frontimage	= $_POST['girl_frontimage'];	$backimage	= $_POST['girl_backimage'];	update_option('girl_frontimage', $frontimage);	update_option('girl_backimage', $backimage);}?>
<div role="main" id="wpbody">	<div tabindex="0" aria-label="Main content" id="wpbody-content">		<div class="wrap">			<h1 class="wp-heading-inline">Upload Cover Images</h1>			<br><br>			<?php echo isset($_SESSION['msg'])?$_SESSION['msg']:""; ?>			<div class="option">				<form name="coverform" action="" method="POST" enctype="multipart/form-data">					<table class="form-table virtue-table">						<tr class="user-rich-editing-wrap">							<th scope="row"><label for="frontimage">Girl's Front Cover</label></th>							<td><input id="girl_frontimage" type="hidden" name="girl_frontimage" value="<?php if($frontimage != ''){ ?><?php echo $frontimage; ?><?php } ?>" /><input class="onetarek-upload-button button staff_button" type="button" value="Upload Image" /></td>						</tr>						<tr class="user-rich-editing-wrap">							<th scope="row"><label for="backimage">Girl's Back Cover</label></th>							<td><input id="girl_backimage" type="hidden" name="girl_backimage" value="<?php if($backimage != ''){ ?><?php echo $backimage; ?><?php } ?>" /><input class="onetarek-upload-button button staff_button" type="button" value="Upload Image" /></td>						</tr>					</table>					<p class="submit"><input type="submit" name="submit_coverform" id="submit_coverform" class="button button-primary" value="Save"></p>					<div class="sidebar-image" style="float: left;">					<h2>Girl's Cover Images</h2>						<img id="imgadd" src="<?php if($frontimage != ''){ ?><?php echo $frontimage; ?><?php }else{ echo plugins_url().'/wp-book-virtue/noimage.png'; } ?>" alt="Virtue Image" />												<img id="imgadd" src="<?php if($backimage != ''){ ?><?php echo $backimage; ?><?php }else{ echo plugins_url().'/wp-book-virtue/noimage.png'; } ?>" alt="Virtue Image" />					</div>									</form>				
			</div>
		</div>
	</div>
</div>